import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import { useChat } from '../context/ChatContext';
import { ArrowLeft, Play, Terminal } from 'lucide-react';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { QuizView } from './QuizView';

interface CourseData {
    id: string;
    title: string;
    description?: string;
    modules: any[];
}

export const CoursePlayer: React.FC = () => {
    const { slug } = useParams();
    const navigate = useNavigate();
    const { openChat } = useChat();
    const [course, setCourse] = useState<CourseData | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<unknown>(null);
    const [currentModuleIndex, setCurrentModuleIndex] = useState(0);
    const [completedModules, setCompletedModules] = useState<Set<string>>(new Set());

    const markAsComplete = async (moduleId: string) => {
        try {
            await api.post('/courses/progress', { moduleId, completed: true });
            setCompletedModules(prev => new Set(prev).add(moduleId));
        } catch (err) {
            console.error('Failed to save progress', err);
        }
    };

    const handleNext = () => {
        if (!course) return;
        const currentModule = course.modules[currentModuleIndex];

        if (currentModule) {
            if (currentModule.type === 'TEXT') {
                markAsComplete(currentModule.id);
            }
        }

        if (currentModuleIndex < course.modules.length - 1) {
            setCurrentModuleIndex(prev => prev + 1);
        } else {
            if (currentModule?.type === 'TEXT') {
                markAsComplete(currentModule.id);
            }
            navigate('/courses');
        }
    };

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const res = await api.get(`/courses/${slug}`);
                setCourse(res.data);
                if (res.data.completedModuleIds) {
                    setCompletedModules(new Set(res.data.completedModuleIds));
                }
            } catch (err: unknown) {
                console.error(err);
                if (err instanceof Error) {
                    setError(err.message);
                } else {
                    setError('Unknown error');
                }
            } finally {
                setLoading(false);
            }
        };
        fetchCourse();
    }, [slug, navigate]);

    if (error) return (
        <div className="p-20 text-center relative z-10 glass-premium w-full max-w-2xl mx-auto rounded-3xl mt-20">
            <h2 className="text-purple-500 text-3xl font-orbitron font-bold">CRITICAL FAILURE</h2>
            <pre className="text-left bg-black/60 border border-white/5 p-6 mt-6 overflow-auto rounded-xl font-mono text-xs text-gray-400">{JSON.stringify(error, null, 2)}</pre>
            <p className="mt-6 text-gray-400 font-mono tracking-widest">AWAITING SYSTEM RESTORE...</p>
            <button onClick={() => navigate('/courses')} className="premium-button mt-8">ABORT</button>
        </div>
    );

    if (loading) return (
        <div className="flex flex-col items-center justify-center h-[60vh] text-cyan-400 font-mono tracking-[0.2em] relative">
            <div className="absolute w-[300px] h-[300px] bg-cyan-500/10 blur-[50px] rounded-full animate-pulse-slow"></div>
            <Terminal size={48} className="animate-pulse mb-6 opacity-80" />
            <p className="animate-pulse">ESTABLISHING SECURE PROTOCOL...</p>
        </div>
    );

    if (!course) return (
        <div className="flex flex-col items-center justify-center h-[60vh] text-center glass-premium rounded-3xl border border-white/5 mt-10">
            <div className="absolute w-[400px] h-[400px] bg-red-500/5 blur-[100px] rounded-full"></div>
            <h2 className="text-4xl text-red-500 font-orbitron mb-4 font-bold relative z-10 drop-shadow-[0_0_10px_rgba(239,68,68,0.5)]">ACCESS DENIED</h2>
            <p className="text-gray-400 mb-8 font-mono tracking-widest relative z-10">THE REQUESTED NODE DOES NOT EXIST</p>
            <button onClick={() => navigate('/courses')} className="premium-button relative z-10">RETURN TO BASE</button>
        </div>
    );

    const currentModule = course.modules[currentModuleIndex];

    if (!currentModule && course.modules.length === 0) return (
        <div className="flex flex-col items-center justify-center h-[60vh] text-center glass-premium rounded-3xl mt-10">
            <h2 className="text-4xl text-purple-400 font-orbitron mb-4 font-bold">EMPTY DIRECTORY</h2>
            <p className="text-gray-400 mb-8 font-mono tracking-widest">AWAITING PAYLOAD INJECTION...</p>
            <button onClick={() => navigate('/courses')} className="premium-button">RETURN TO BASE</button>
        </div>
    );

    return (
        <div className="h-[calc(100vh-140px)] flex flex-col lg:flex-row gap-8 animate-in fade-in duration-1000 relative">
            {/* Sidebar (Modules List) */}
            <div className="w-full lg:w-80 flex-shrink-0 flex flex-col gap-6 relative z-10">
                <button
                    onClick={() => navigate('/courses')}
                    className="flex items-center gap-3 text-gray-400 hover:text-cyan-400 transition-colors text-xs font-bold tracking-widest uppercase font-mono"
                >
                    <ArrowLeft size={16} /> ABORT OPERATION
                </button>

                <div className="glass-premium flex-grow rounded-2xl overflow-hidden flex flex-col border border-white/5 shadow-[0_8px_32px_rgba(0,0,0,0.5)]">
                    <div className="p-6 border-b border-white/5 bg-black/40 relative">
                        <h2 className="font-bold text-white font-orbitron text-sm tracking-widest">MODULE SEQUENCE</h2>
                        <div className="h-1 w-20 bg-gradient-to-r from-cyan-400 to-purple-500 mt-4 rounded-full"></div>
                    </div>
                    <div className="overflow-y-auto p-4 space-y-2 custom-scrollbar flex-grow bg-black/20">
                        {course.modules.map((mod, idx: number) => {
                            const isCompleted = completedModules.has(mod.id);
                            return (
                                <button
                                    key={mod.id}
                                    onClick={() => setCurrentModuleIndex(idx)}
                                    className={`w-full text-left p-4 rounded-xl text-sm flex items-center gap-4 transition-all duration-300 ${idx === currentModuleIndex
                                        ? 'bg-cyan-500/10 border border-cyan-400/50 text-white shadow-[0_0_20px_rgba(0,240,255,0.15)]'
                                        : 'hover:bg-white/5 text-gray-400 hover:text-gray-200 border border-transparent'
                                        }`}
                                >
                                    <div className={`w-8 h-8 rounded-lg flex items-center justify-center text-xs font-mono border transition-colors ${idx === currentModuleIndex
                                        ? 'border-cyan-400 text-cyan-400 shadow-[0_0_10px_rgba(0,240,255,0.3)_inset]'
                                        : isCompleted
                                            ? 'border-purple-500 text-purple-400 bg-purple-500/10 shadow-[0_0_10px_rgba(189,0,255,0.2)_inset]'
                                            : 'border-gray-700 text-gray-600 bg-black/40'
                                        }`}>
                                        {isCompleted ? '✓' : idx + 1}
                                    </div>
                                    <span className="line-clamp-2 font-medium leading-relaxed">{mod.title}</span>
                                </button>
                            );
                        })}
                    </div>
                </div>
            </div>

            {/* Main Content Area */}
            <div className="flex-grow flex flex-col min-h-0 relative">
                <div className="absolute right-0 top-0 w-[500px] h-[500px] bg-cyan-500/5 blur-[120px] rounded-full pointer-events-none -translate-y-1/3 translate-x-1/3"></div>
                <div className="glass-premium rounded-2xl flex-grow overflow-hidden flex flex-col border border-white/5 relative z-10 shadow-[0_8px_32px_rgba(0,0,0,0.5)]">

                    {/* Header */}
                    <div className="p-8 border-b border-white/5 bg-black/60 flex flex-col md:flex-row justify-between items-start md:items-center gap-6 relative overflow-hidden">
                        <div className="absolute inset-0 bg-gradient-to-r from-transparent via-cyan-500/5 to-transparent bg-[length:200%_100%] animate-cyber-border-anim opacity-50 z-0"></div>
                        <div className="relative z-10">
                            <h1 className="text-3xl font-bold text-white font-orbitron mb-3 drop-shadow-md">{currentModule?.title}</h1>
                            <p className="text-gray-400 text-xs flex items-center gap-3 tracking-widest font-mono uppercase">
                                <Terminal size={12} className="text-purple-400 animate-pulse-slow" />
                                {course.title}
                            </p>
                        </div>
                        <div className="flex gap-4 relative z-10">
                            <button
                                onClick={openChat}
                                className="px-5 py-2.5 rounded-lg border bg-black/80 text-cyan-400 border-cyan-400/40 hover:bg-cyan-500/10 font-bold tracking-widest text-xs flex items-center gap-3 transition-all duration-300 shadow-[0_0_15px_rgba(0,240,255,0.1)_inset]"
                            >
                                <Terminal size={14} /> MESHARK PROTOCOL
                            </button>
                        </div>
                    </div>

                    {/* Content Viewer */}
                    <div className="flex-grow overflow-y-auto p-10 custom-scrollbar bg-black/40 relative z-10">
                        {currentModule?.type === 'QUIZ' ? (
                            <div className="max-w-4xl mx-auto">
                                <h2 className="text-3xl font-orbitron text-cyan-400 mb-8 border-b border-white/10 pb-4 drop-shadow-[0_0_5px_rgba(0,240,255,0.5)] flex items-center gap-4">
                                    <Terminal size={28} />
                                    Security Clearance Exam
                                </h2>
                                <QuizView
                                    content={currentModule.content || '[]'}
                                    onComplete={(passed) => {
                                        if (passed) {
                                            markAsComplete(currentModule.id);
                                        }
                                    }}
                                />
                            </div>
                        ) : (
                            <div className="prose prose-invert lg:prose-lg prose-p:text-gray-300 prose-p:leading-relaxed prose-headings:font-orbitron prose-headings:text-gray-100 prose-headings:tracking-wide prose-a:text-purple-400 prose-a:no-underline hover:prose-a:underline prose-strong:text-white prose-strong:font-bold prose-code:text-cyan-400 prose-pre:bg-[#0a0a0a] prose-pre:border prose-pre:border-white/10 prose-pre:shadow-[0_4px_20px_rgba(0,0,0,0.5)] max-w-4xl mx-auto">
                                {currentModule?.content ? (
                                    <ReactMarkdown remarkPlugins={[remarkGfm]}>
                                        {currentModule.content}
                                    </ReactMarkdown>
                                ) : (
                                    <div className="text-gray-500 font-mono tracking-widest text-center mt-20">DECRYPTION FAILED. PAYLOAD EMPTY.</div>
                                )}
                            </div>
                        )}
                    </div>

                    {/* Footer / Controls */}
                    <div className="p-6 border-t border-white/5 bg-black/60 flex justify-between items-center relative z-10">
                        <button
                            disabled={currentModuleIndex === 0}
                            onClick={() => setCurrentModuleIndex(prev => prev - 1)}
                            className="text-xs tracking-widest text-gray-400 font-bold hover:text-white disabled:opacity-20 disabled:hover:text-gray-400 transition-colors uppercase font-mono"
                        >
                            <ArrowLeft size={14} className="inline mr-2" />
                            Prev Vector
                        </button>
                        <button
                            onClick={handleNext}
                            className="premium-button text-xs py-3 px-8 tracking-widest flex items-center gap-3 bg-black/50"
                        >
                            {currentModuleIndex === course.modules.length - 1 ? 'FINALIZE PROTOCOL' : 'NEXT NODE'}
                            <Play size={14} fill="currentColor" />
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { Link } from 'react-router-dom';
import { BookOpen, Terminal, Cpu } from 'lucide-react';

interface Course {
    id: string;
    title: string;
    slug: string;
    description: string;
    instructor: {
        username: string;
    };
    modules: any[];
}

export const Courses: React.FC = () => {
    const [courses, setCourses] = useState<Course[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCourses = async () => {
            try {
                const res = await api.get('/courses');
                setCourses(res.data);
            } catch (err: unknown) {
                console.error('Failed to fetch courses', err);
            } finally {
                setLoading(false);
            }
        };
        fetchCourses();
    }, []);

    return (
        <div className="space-y-12 animate-in fade-in duration-1000">
            <div className="flex flex-col md:flex-row justify-between items-start md:items-end border-b border-white/5 pb-8 relative">
                <div className="absolute top-0 left-1/4 w-96 h-96 bg-purple-500/10 blur-[100px] rounded-full pointer-events-none -translate-y-1/2"></div>
                <div className="relative z-10 space-y-4">
                    <h1 className="text-5xl font-bold text-white font-orbitron tracking-wide">
                        DATABANKS & <span className="text-gradient-primary">MODULES</span>
                    </h1>
                    <p className="text-gray-400 font-mono text-sm max-w-2xl leading-relaxed">
                        Access premium operational databases and theoretical frameworks. High-level security clearance verification required for advanced curriculum navigation.
                    </p>
                </div>
                <div className="hidden md:block text-right relative z-10 mt-6 md:mt-0 glass-premium px-6 py-4 rounded-xl border border-white/5">
                    <div className="text-4xl font-bold text-cyan-400 font-orbitron drop-shadow-[0_0_8px_rgba(0,240,255,0.5)]">
                        {courses.length.toString().padStart(2, '0')}
                    </div>
                    <div className="text-xs text-gray-500 uppercase tracking-widest mt-1">Available Streams</div>
                </div>
            </div>

            {loading ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {[1, 2, 3].map((i) => (
                        <div key={i} className="h-[300px] glass-premium rounded-2xl animate-pulse"></div>
                    ))}
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 relative z-10">
                    {courses.map((course) => (
                        <Link
                            to={`/courses/${course.slug}`}
                            key={course.id}
                            className="group glass-premium rounded-2xl p-8 hover:border-cyan-500/50 hover:shadow-[0_10px_40px_rgba(0,240,255,0.15)] transition-all duration-500 hover:-translate-y-2 relative overflow-hidden flex flex-col h-[320px]"
                        >
                            <div className="absolute top-0 right-0 p-4 opacity-5 group-hover:opacity-20 group-hover:text-purple-400 transition-all duration-500 transform group-hover:scale-110">
                                <Cpu size={120} />
                            </div>

                            <div className="mb-6 relative z-10">
                                <span className="text-[10px] font-mono tracking-widest uppercase border border-cyan-400/30 text-cyan-400 px-3 py-1.5 rounded-full shadow-[0_0_10px_rgba(0,240,255,0.1)_inset]">
                                    {course.instructor.username === 'admin' ? 'OFFICIAL CURRICULUM' : 'COMMUNITY'}
                                </span>
                            </div>

                            <h3 className="text-2xl font-bold text-gray-100 mb-3 font-orbitron group-hover:text-white transition-colors line-clamp-2 relative z-10">
                                {course.title}
                            </h3>

                            <p className="text-gray-400 text-sm mb-6 flex-grow line-clamp-3 leading-relaxed relative z-10 group-hover:text-gray-300 transition-colors">
                                {course.description || "No briefing available for this operation."}
                            </p>

                            <div className="flex items-center justify-between border-t border-white/5 pt-5 mt-auto relative z-10">
                                <div className="flex items-center gap-3 text-gray-500 text-xs font-mono tracking-widest">
                                    <BookOpen size={16} className="text-purple-400/70" />
                                    <span>TEXT MODULE</span>
                                </div>
                                <div className="text-cyan-400 text-xs font-bold uppercase tracking-widest group-hover:translate-x-2 transition-transform duration-300 flex items-center gap-2">
                                    INITIALIZE <Terminal size={14} className="animate-pulse-slow" />
                                </div>
                            </div>

                            <div className="absolute bottom-0 left-0 h-1.5 w-full bg-gradient-to-r from-cyan-400 via-purple-500 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
                        </Link>
                    ))}
                </div>
            )}
        </div>
    );
};

import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { BookOpen, Terminal, Activity, ArrowRight, ShieldCheck, Clock } from 'lucide-react';
import { Link } from 'react-router-dom';

interface Course {
    id: string;
    title: string;
    description: string;
    slug: string;
    difficulty: string;
    modules: any[];
    progress?: number;
}

export const Dashboard: React.FC = () => {
    const { user } = useAuth();
    const [courses, setCourses] = useState<Course[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCourses = async () => {
            try {
                const res = await api.get('/courses');
                setCourses(res.data);
            } catch (err: unknown) {
                console.error('Failed to load courses', err);
            } finally {
                setLoading(false);
            }
        };
        fetchCourses();
    }, []);

    return (
        <div className="space-y-10 animate-in fade-in duration-1000">
            {/* Header Section */}
            <div className="relative rounded-3xl p-10 overflow-hidden glass-premium cyber-border">
                <div className="absolute top-0 right-0 w-[500px] h-[500px] bg-cyan-500/10 blur-[100px] rounded-full pointer-events-none -translate-y-1/2 translate-x-1/3 animate-pulse-slow"></div>
                <div className="absolute bottom-0 left-0 w-[400px] h-[400px] bg-purple-500/10 blur-[100px] rounded-full pointer-events-none translate-y-1/2 -translate-x-1/3 animate-pulse-slow"></div>

                <div className="relative z-10">
                    <h1 className="text-5xl font-bold text-white mb-4 font-sans tracking-tight">
                        Welcome back, <span className="text-gradient-primary">{user?.username}</span>
                    </h1>
                    <p className="text-gray-400 flex items-center gap-4 text-lg font-light">
                        Level: <span className="text-gray-200 font-mono tracking-widest">Senior Operative</span>
                        <span className="w-1.5 h-1.5 rounded-full bg-cyan-500 shadow-[0_0_10px_rgba(0,240,255,0.8)]"></span>
                        Role: <span className="text-cyan-400 uppercase tracking-widest text-xs border border-cyan-400/30 px-3 py-1 rounded-full shadow-[0_0_15px_rgba(0,240,255,0.2)_inset]">{user?.role}</span>
                    </p>
                </div>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">
                {/* Main Content: Learning Path */}
                <div className="lg:col-span-2 space-y-8">
                    <div className="flex justify-between items-center mb-4">
                        <h2 className="text-2xl font-semibold text-white flex items-center gap-3 font-orbitron tracking-wide">
                            <Terminal size={24} className="text-cyan-400" /> Active Operations
                        </h2>
                    </div>

                    {loading ? (
                        <div className="space-y-6">
                            {[1, 2].map(i => (
                                <div key={i} className="h-32 glass-premium rounded-2xl animate-pulse"></div>
                            ))}
                        </div>
                    ) : courses.length > 0 ? (
                        <div className="space-y-6">
                            {courses.slice(0, 3).map((course) => {
                                const progress = course.progress || 0;
                                return (
                                    <div key={course.id} className="group relative glass-premium hover:border-cyan-500/50 p-8 rounded-2xl transition-all duration-500">
                                        <div className="flex flex-col md:flex-row items-center gap-8 relative z-10">
                                            <div className="p-5 rounded-2xl bg-black/60 border border-white/5 group-hover:border-cyan-500/50 transition-all duration-500 shadow-[0_0_20px_rgba(0,0,0,0.5)] group-hover:shadow-[0_0_20px_rgba(0,240,255,0.2)]">
                                                <ShieldCheck size={32} className={`transition-all duration-500 ${progress > 0 ? 'text-cyan-400 drop-shadow-[0_0_8px_rgba(0,240,255,0.5)]' : 'text-gray-600'}`} />
                                            </div>

                                            <div className="flex-grow text-center md:text-left">
                                                <h3 className="text-xl font-bold text-gray-200 mb-2 group-hover:text-white transition-colors">{course.title}</h3>
                                                <div className="flex items-center justify-center md:justify-start gap-4 text-sm text-gray-500 font-mono tracking-widest">
                                                    <span className="text-purple-400/80">{course.difficulty}</span>
                                                    <span>•</span>
                                                    <span>{course.modules?.length || 0} MODULES</span>
                                                </div>

                                                {/* Progress Bar */}
                                                <div className="mt-5 h-2 w-full bg-black/50 rounded-full overflow-hidden border border-white/5">
                                                    <div
                                                        className="h-full bg-gradient-to-r from-cyan-400 to-purple-500 transition-all duration-1000 ease-out relative"
                                                        style={{ width: `${progress}%` }}
                                                    >
                                                        <div className="absolute inset-0 bg-white/20 w-full animate-pulse"></div>
                                                    </div>
                                                </div>
                                                <div className="flex justify-between mt-2 font-mono tracking-widest">
                                                    <span className="text-[10px] text-gray-500 uppercase">Synchronization</span>
                                                    <span className="text-[10px] text-cyan-400">{progress}%</span>
                                                </div>
                                            </div>

                                            <div className="mt-4 md:mt-0">
                                                <Link
                                                    to={`/courses/${course.slug}`}
                                                    className="premium-button block text-center"
                                                >
                                                    {course.progress && course.progress > 0 ? 'RESUME' : 'INITIATE'}
                                                </Link>
                                            </div>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    ) : (
                        <div className="p-12 text-center text-gray-500 glass-premium rounded-2xl border-dashed border-white/10 font-mono tracking-widest">
                            [ NO ACTIVE MISSIONS ASSIGNED ]
                        </div>
                    )}
                </div>

                {/* Sidebar: Stats & Quick Links */}
                <div className="space-y-10">
                    {/* Stats Card */}
                    <div className="glass-premium p-8 rounded-2xl">
                        <h3 className="text-xs font-bold text-gray-400 uppercase tracking-widest mb-8 flex items-center gap-3 font-orbitron">
                            <Activity size={18} className="text-purple-400" /> Operator Metrics
                        </h3>
                        <div className="grid grid-cols-2 gap-4">
                            <div className="p-5 bg-black/40 rounded-xl border border-white/5 text-center group hover:border-purple-500/30 transition-colors">
                                <div className="text-3xl font-bold text-white mb-2 font-orbitron">1%</div>
                                <div className="text-[10px] text-gray-500 uppercase tracking-widest">Global Rank</div>
                            </div>
                            <div className="p-5 bg-black/40 rounded-xl border border-white/5 text-center group hover:border-cyan-500/30 transition-colors">
                                <div className="text-3xl font-bold text-cyan-400 mb-2 font-orbitron drop-shadow-[0_0_8px_rgba(0,240,255,0.5)]">{courses.length}</div>
                                <div className="text-[10px] text-gray-500 uppercase tracking-widest">Active Ops</div>
                            </div>
                            <div className="p-5 bg-black/40 rounded-xl border border-white/5 text-center col-span-2 group hover:border-white/20 transition-colors">
                                <div className="text-3xl font-bold text-white mb-2 font-orbitron">128H</div>
                                <div className="text-[10px] text-gray-500 flex items-center justify-center gap-2 uppercase tracking-widest">
                                    <Clock size={12} className="text-purple-400" /> Total Field Time
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Recommended Resources */}
                    <div className="relative glass-premium p-8 rounded-2xl overflow-hidden">
                        <div className="absolute top-0 right-0 w-32 h-32 bg-purple-500/10 blur-2xl rounded-full"></div>
                        <h3 className="text-xs font-bold text-gray-400 uppercase tracking-widest mb-6 flex items-center gap-3 font-orbitron relative z-10">
                            <BookOpen size={18} className="text-cyan-400" /> Latest Intel
                        </h3>
                        <div className="space-y-5 relative z-10">
                            {[
                                { title: "OWASP Top 10: 2026 Directives", date: "T-0" },
                                { title: "Zero-Day Analysis: Kernel Ops", date: "T-24H" },
